import { useState, useEffect } from "react";
import { MessageCircle, X, Send } from "lucide-react";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import AIMentorIcon from "./AIMentorIcon";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { useMutation, useQuery } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export default function FloatingChatButton() {
  const [isOpen, setIsOpen] = useState(false);
  const [message, setMessage] = useState("");
  const [showAssistancePopup, setShowAssistancePopup] = useState(false);
  const [hasShownPopup, setHasShownPopup] = useState(false);
  const { toast } = useToast();

  // Show assistance popup after user spends time on website
  useEffect(() => {
    if (hasShownPopup || isOpen) return;

    const showPopupTimer = setTimeout(() => {
      setShowAssistancePopup(true);
      setHasShownPopup(true);
    }, 10000); // Show after 10 seconds (reduced for demo)

    return () => clearTimeout(showPopupTimer);
  }, [hasShownPopup, isOpen]);

  // Auto-hide popup after some time
  useEffect(() => {
    if (!showAssistancePopup) return;

    const hidePopupTimer = setTimeout(() => {
      setShowAssistancePopup(false);
    }, 8000); // Hide after 8 seconds

    return () => clearTimeout(hidePopupTimer);
  }, [showAssistancePopup]);

  const { data: chatMessages, isLoading } = useQuery({
    queryKey: ["/api/student/chat"],
    enabled: isOpen,
  });

  const sendMessageMutation = useMutation({
    mutationFn: async (message: string) => {
      const response = await apiRequest("POST", "/api/student/chat", { message });
      return response.json();
    },
    onSuccess: () => {
      setMessage("");
      queryClient.invalidateQueries({ queryKey: ["/api/student/chat"] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    if (message.trim()) {
      sendMessageMutation.mutate(message);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const toggleChat = () => {
    setIsOpen(!isOpen);
    setShowAssistancePopup(false); // Hide popup when chat is opened
  };

  const handleAssistanceClick = () => {
    setShowAssistancePopup(false);
    setIsOpen(true);
  };

  const dismissPopup = () => {
    setShowAssistancePopup(false);
  };

  // Sort messages by date and group user/AI pairs
  const sortedMessages = Array.isArray(chatMessages) ? chatMessages.sort((a: any, b: any) => 
    new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime()
  ) : [];

  return (
    <div className="fixed bottom-4 right-4 z-50 sm:bottom-6 sm:right-6">
      {/* Assistance Popup */}
      {showAssistancePopup && !isOpen && (
        <div className="absolute bottom-16 right-0 w-64 bg-white rounded-lg shadow-xl border border-gray-200 p-4 animate-in slide-in-from-right-2 fade-in duration-300">
          <div className="flex items-start space-x-3">
            <div className="w-10 h-10 bg-gradient-to-br from-primary to-secondary rounded-full flex items-center justify-center flex-shrink-0">
              <AIMentorIcon className="text-white h-5 w-5" />
            </div>
            <div className="flex-1">
              <h3 className="text-sm font-semibold text-gray-900 mb-1">Need assistance?</h3>
              <p className="text-xs text-gray-600 mb-3">
                I'm your AI mentor! I can help you with academic planning, finding opportunities, and answering questions about your pathway to college.
              </p>
              <div className="flex space-x-2">
                <Button
                  onClick={handleAssistanceClick}
                  size="sm"
                  className="text-xs bg-primary hover:bg-primary/90 h-7"
                >
                  Get Help
                </Button>
                <Button
                  onClick={dismissPopup}
                  variant="outline"
                  size="sm"
                  className="text-xs h-7"
                >
                  Not now
                </Button>
              </div>
            </div>
            <Button
              onClick={dismissPopup}
              variant="ghost"
              size="sm"
              className="p-1 h-auto text-gray-400 hover:text-gray-600"
            >
              <X className="h-3 w-3" />
            </Button>
          </div>
        </div>
      )}

      {/* Chat Interface */}
      {isOpen && (
        <div className="absolute bottom-16 right-0 w-[calc(100vw-2rem)] max-w-sm sm:w-80 sm:max-w-none bg-white rounded-lg shadow-xl border border-gray-200 max-h-[80vh] sm:max-h-96 flex flex-col">
          <CardHeader className="bg-gradient-to-r from-primary to-secondary text-white rounded-t-lg flex-shrink-0 py-3">
            <div className="flex items-center justify-between">
              <CardTitle className="text-base sm:text-lg font-semibold flex items-center">
                <AIMentorIcon className="mr-2 h-4 w-4 sm:h-5 sm:w-5" />
                AI Mentor
              </CardTitle>
              <Button 
                variant="ghost" 
                size="sm" 
                onClick={toggleChat}
                className="text-white hover:bg-white/20 p-1 h-auto"
              >
                <X className="h-4 w-4" />
              </Button>
            </div>
          </CardHeader>

          <CardContent className="flex-1 flex flex-col p-3 sm:p-4 min-h-0">
            {/* Messages */}
            <div className="flex-1 space-y-3 mb-3 overflow-y-auto overscroll-contain chat-scrollbar">
              {isLoading ? (
                <div className="text-center text-gray-500 py-8">
                  <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-primary mx-auto"></div>
                  <p className="text-sm mt-2">Loading messages...</p>
                </div>
              ) : sortedMessages.length > 0 ? (
                sortedMessages.map((msg: any) => (
                  <div
                    key={msg.id}
                    className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div
                      className={`max-w-[85%] sm:max-w-xs px-3 py-2 rounded-lg text-sm leading-relaxed ${
                        msg.sender === 'user'
                          ? 'bg-primary text-white rounded-br-sm'
                          : 'bg-gray-100 text-gray-900 rounded-bl-sm'
                      }`}
                    >
                      {msg.sender === 'ai' && (
                        <div className="flex items-center mb-1">
                          <AIMentorIcon className="mr-1 h-3 w-3" />
                          <span className="text-xs font-medium text-primary">AIMS</span>
                        </div>
                      )}
                      <p className="whitespace-pre-wrap break-words">{msg.message}</p>
                      <div className="text-xs opacity-70 mt-1">
                        {new Date(msg.createdAt).toLocaleTimeString([], { 
                          hour: '2-digit', 
                          minute: '2-digit' 
                        })}
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="flex items-start space-x-2 py-4">
                  <div className="w-8 h-8 bg-gradient-to-br from-primary to-secondary rounded-full flex items-center justify-center flex-shrink-0">
                    <AIMentorIcon className="text-white text-xs h-5 w-5" />
                  </div>
                  <div className="bg-gray-50 rounded-lg p-3 text-sm rounded-bl-sm border border-gray-200">
                    <div className="prose prose-sm max-w-none">
                      <ReactMarkdown 
                        remarkPlugins={[remarkGfm]}
                        components={{
                          p: ({children}) => <p className="text-gray-700 mb-1 leading-relaxed text-sm">{children}</p>,
                          strong: ({children}) => <strong className="font-semibold text-gray-900">{children}</strong>,
                          ul: ({children}) => <ul className="list-disc list-inside space-y-0.5 mb-1 text-gray-700 text-sm">{children}</ul>,
                          li: ({children}) => <li className="ml-1 text-sm">{children}</li>,
                        }}
                      >
                        {`**Hi! I'm your AI Academic Mentor** 🎓

I can help you with:
• Academic planning & course selection
• College preparation strategies
• Finding opportunities & competitions  
• Test prep guidance

**Ask me anything about your academic journey!**`}
                      </ReactMarkdown>
                    </div>
                  </div>
                </div>
              )}
            </div>

            {/* Input */}
            <div className="flex items-center space-x-2 border-t pt-3 flex-shrink-0">
              <Input
                type="text"
                placeholder="Type your message..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                className="flex-1 text-sm focus:ring-2 focus:ring-primary focus:border-transparent min-h-[44px] py-3 chat-input"
                disabled={sendMessageMutation.isPending}
              />
              <Button
                onClick={handleSendMessage}
                disabled={sendMessageMutation.isPending || !message.trim()}
                className="bg-primary hover:bg-primary/90 min-h-[44px] min-w-[44px] h-11 w-11 p-0 flex-shrink-0 mobile-touch-target"
              >
                {sendMessageMutation.isPending ? (
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                ) : (
                  <Send className="h-4 w-4" />
                )}
              </Button>
            </div>
          </CardContent>
        </div>
      )}

      {/* Floating Button */}
      <Button
        onClick={toggleChat}
        className="w-14 h-14 sm:w-16 sm:h-16 bg-gradient-to-br from-primary to-secondary text-white rounded-full shadow-lg hover:shadow-xl transition-all duration-200 flex items-center justify-center group active:scale-95 mobile-touch-target"
      >
        {isOpen ? (
          <X className="h-6 w-6 sm:h-7 sm:w-7 group-hover:scale-110 transition-transform" />
        ) : (
          <AIMentorIcon className="h-8 w-8 sm:h-9 sm:w-9 group-hover:scale-110 transition-transform" />
        )}

        {/* Notification pulse for assistance */}
        {showAssistancePopup && !isOpen && (
          <div className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full animate-pulse"></div>
        )}
      </Button>
    </div>
  );
}

server/services/pathwayGenerator.ts.content:
import OpenAI from "openai";
import { StudentProfile } from "@shared/schema";

const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR || "default_key"
});

export async function generateAcademicPathway(studentProfile: StudentProfile): Promise<{
  grade9: any[];
  grade10: any[];
  grade11: any[];
  grade12: any[];
}> {
  try {
    const prompt = `Generate a comprehensive 4-year academic pathway for a student with the following profile:

Current Grade: ${studentProfile.currentGrade}
Current GPA: ${studentProfile.currentGpa || "Not provided"}
Dream Colleges: ${studentProfile.dreamColleges?.join(", ") || "Top Universities"}
Academic Interests: ${studentProfile.academicInterests?.join(", ") || "General"}
Career Goals: ${studentProfile.careerGoals || "Not specified"}
Current Subjects: ${studentProfile.currentSubjects?.join(", ") || "Standard curriculum"}
Interested Subjects: ${studentProfile.interestedSubjects?.join(", ") || "Various"}
Location: ${studentProfile.location || "Not specified"}
School District: ${studentProfile.schoolDistrict || "Not specified"}

Create a detailed academic pathway with specific, actionable tasks for each grade level (9-12). Each grade should include:
- Core academic courses (including specific AP/IB recommendations)
- Extracurricular activities and leadership opportunities
- Competitions and contests relevant to their interests
- Test preparation milestones
- Summer activities (internships, programs, etc.)
- Volunteer work and community service
- Research projects or independent studies
- Skills development activities

Provide realistic, achievable goals that build upon each other and align with their dream colleges and career interests.

Respond with JSON in this exact format:
{
  "grade9": [
    {
      "title": "Course/Activity Name",
      "category": "academic|extracurricular|competition|volunteer|summer|test_prep",
      "description": "Detailed description",
      "timeline": "When to complete (e.g., Fall, Spring, Summer)",
      "priority": "high|medium|low",
      "status": "completed|in_progress|not_started"
    }
  ],
  "grade10": [...],
  "grade11": [...],
  "grade12": [...]
}`;

    const response = await openai.chat.completions.create({
      model: "gpt-4o", // the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
      messages: [{ role: "user", content: prompt }],
      response_format: { type: "json_object" },
      temperature: 0.4,
    });

    const pathwayData = JSON.parse(response.choices[0].message.content || "{}");

    // Validate the response structure
    const grades = ['grade9', 'grade10', 'grade11', 'grade12'];
    const validatedPathway: any = {};

    for (const grade of grades) {
      validatedPathway[grade] = Array.isArray(pathwayData[grade]) ? pathwayData[grade] : [];
    }

    return validatedPathway;
  } catch (error) {
    console.error("Error generating academic pathway:", error);

    // Return a fallback pathway structure
    return {
      grade9: [
        {
          title: "Build Strong Academic Foundation",
          category: "academic",
          description: "Focus on core subjects and maintain high GPA",
          timeline: "Year-round",
          priority: "high",
          status: "in_progress"
        },
        {
          title: "Join Academic Clubs",
          category: "extracurricular",
          description: "Participate in Math, Science, or relevant subject clubs",
          timeline: "Fall",
          priority: "medium",
          status: "not_started"
        }
      ],
      grade10: [
        {
          title: "Take First AP Course",
          category: "academic",
          description: "Enroll in AP course aligned with interests",
          timeline: "Year-round",
          priority: "high",
          status: "not_started"
        },
        {
          title: "PSAT Preparation",
          category: "test_prep",
          description: "Begin standardized test preparation",
          timeline: "Spring",
          priority: "medium",
          status: "not_started"
        }
      ],
      grade11: [
        {
          title: "Advanced Course Load",
          category: "academic",
          description: "Take multiple AP/IB courses",
          timeline: "Year-round",
          priority: "high",
          status: "not_started"
        },
        {
          title: "SAT/ACT Preparation",
          category: "test_prep",
          description: "Intensive test preparation and taking",
          timeline: "Fall/Spring",
          priority: "high",
          status: "not_started"
        }
      ],
      grade12: [
        {
          title: "College Applications",
          category: "academic",
          description: "Complete and submit college applications",
          timeline: "Fall",
          priority: "high",
          status: "not_started"
        },
        {
          title: "Final Transcript",
          category: "academic",
          description: "Maintain strong grades through graduation",
          timeline: "Year-round",
          priority: "high",
          status: "not_started"
        }
      ]
    };
  }
}

export async function generateTodosFromPathway(pathwayData: any, currentGrade: number): Promise<{
  title: string;
  description: string;
  priority: string;
  category: string;
  dueDate?: Date;
}[]> {
  try {
    const currentGradeKey = `grade${currentGrade}`;
    const currentGradeTasks = pathwayData[currentGradeKey] || [];

    const todos = currentGradeTasks
      .filter((task: any) => task.status === "not_started" || task.status === "in_progress")
      .slice(0, 5) // Limit to 5 most important tasks
      .map((task: any) => ({
        title: task.title,
        description: task.description,
        priority: task.priority,
        category: task.category,
        dueDate: task.timeline === "Fall" ? new Date(new Date().getFullYear(), 11, 31) : // December 31
                 task.timeline === "Spring" ? new Date(new Date().getFullYear() + 1, 4, 31) : // May 31
                 task.timeline === "Summer" ? new Date(new Date().getFullYear() + 1, 7, 31) : // August 31
                 undefined
      }));

    return todos;
  } catch (error) {
    console.error("Error generating todos from pathway:", error);
    return [];
  }
}

client/src/components/ui/sidebar.tsx.content:
import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { VariantProps, cva } from "class-variance-authority"
import { PanelLeft } from "lucide-react"

import { useIsMobile } from "@/hooks/use-mobile"
import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Separator } from "@/components/ui/separator"
import {
  Sheet,
  SheetContent,
  SheetDescription,
  SheetHeader,
  SheetTitle,
} from "@/components/ui/sheet"
import { Skeleton } from "@/components/ui/skeleton"
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip"

const SIDEBAR_COOKIE_NAME = "sidebar_state"
const SIDEBAR_COOKIE_MAX_AGE = 60 * 60 * 24 * 7
const SIDEBAR_WIDTH = "16rem"
const SIDEBAR_WIDTH_MOBILE = "18rem"
const SIDEBAR_WIDTH_ICON = "3rem"
const SIDEBAR_KEYBOARD_SHORTCUT = "b"

type SidebarContextProps = {
  state: "expanded" | "collapsed"
  open: boolean
  setOpen: (open: boolean) => void
  openMobile: boolean
  setOpenMobile: (open: boolean) => void
  isMobile: boolean
  toggleSidebar: () => void
}

const SidebarContext = React.createContext<SidebarContextProps | null>(null)

function useSidebar() {
  const context = React.useContext(SidebarContext)
  if (!context) {
    throw new Error("useSidebar must be used within a SidebarProvider.")
  }

  return context
}

const SidebarProvider = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    defaultOpen?: boolean
    open?: boolean
    onOpenChange?: (open: boolean) => void
  }
>(
  (
    {
      defaultOpen = true,
      open: openProp,
      onOpenChange: setOpenProp,
      className,
      style,
      children,
      ...props
    },
    ref
  ) => {
    const isMobile = useIsMobile()
    const [openMobile, setOpenMobile] = React.useState(false)

    // This is the internal state of the sidebar.
    // We use openProp and setOpenProp for control from outside the component.
    const [_open, _setOpen] = React.useState(defaultOpen)
    const open = openProp ?? _open
    const setOpen = React.useCallback(
      (value: boolean | ((value: boolean) => boolean)) => {
        const openState = typeof value === "function" ? value(open) : value
        if (setOpenProp) {
          setOpenProp(openState)
        } else {
          _setOpen(openState)
        }

        // This sets the cookie to keep the sidebar state.
        document.cookie = `${SIDEBAR_COOKIE_NAME}=${openState}; path=/; max-age=${SIDEBAR_COOKIE_MAX_AGE}`
      },
      [setOpenProp, open]
    )

    // Helper to toggle the sidebar.
    const toggleSidebar = React.useCallback(() => {
      return isMobile
        ? setOpenMobile((open) => !open)
        : setOpen((open) => !open)
    }, [isMobile, setOpen, setOpenMobile])

    // Adds a keyboard shortcut to toggle the sidebar.
    React.useEffect(() => {
      const handleKeyDown = (event: KeyboardEvent) => {
        if (
          event.key === SIDEBAR_KEYBOARD_SHORTCUT &&
          (event.metaKey || event.ctrlKey)
        ) {
          event.preventDefault()
          toggleSidebar()
        }
      }

      window.addEventListener("keydown", handleKeyDown)
      return () => window.removeEventListener("keydown", handleKeyDown)
    }, [toggleSidebar])

    // We add a state so that we can do data-state="expanded" or "collapsed".
    // This makes it easier to style the sidebar with Tailwind classes.
    const state = open ? "expanded" : "collapsed"

    const contextValue = React.useMemo<SidebarContextProps>(
      () => ({
        state,
        open,
        setOpen,
        isMobile,
        openMobile,
        setOpenMobile,
        toggleSidebar,
      }),
      [state, open, setOpen, isMobile, openMobile, setOpenMobile, toggleSidebar]
    )

    return (
      <SidebarContext.Provider value={contextValue}>
        <TooltipProvider delayDuration={0}>
          <div
            style={
              {
                "--sidebar-width": SIDEBAR_WIDTH,
                "--sidebar-width-icon": SIDEBAR_WIDTH_ICON,
                ...style,
              } as React.CSSProperties
            }
            className={cn(
              "group/sidebar-wrapper flex min-h-svh w-full has-[[data-variant=inset]]:bg-sidebar",
              className
            )}
            ref={ref}
            {...props}
          >
            {children}
          </div>
        </TooltipProvider>
      </SidebarContext.Provider>
    )
  }
)
SidebarProvider.displayName = "SidebarProvider"

const Sidebar = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    side?: "left" | "right"
    variant?: "sidebar" | "floating" | "inset"
    collapsible?: "offcanvas" | "icon" | "none"
  }
>(
  (
    {
      side = "left",
      variant = "sidebar",
      collapsible = "offcanvas",
      className,
      children,
      ...props
    },
    ref
  ) => {
    const { isMobile, state, openMobile, setOpenMobile } = useSidebar()

    if (collapsible === "none") {
      return (
        <div
          className={cn(
            "flex h-full w-[--sidebar-width] flex-col bg-sidebar text-sidebar-foreground",
            className
          )}
          ref={ref}
          {...props}
        >
          {children}
        </div>
      )
    }

    if (isMobile) {
      return (
        <Sheet open={openMobile} onOpenChange={setOpenMobile} {...props}>
          <SheetContent
            data-sidebar="sidebar"
            data-mobile="true"
            className="w-[--sidebar-width] bg-sidebar p-0 text-sidebar-foreground [&>button]:hidden"
            style={
              {
                "--sidebar-width": SIDEBAR_WIDTH_MOBILE,
              } as React.CSSProperties
            }
            side={side}
          >
            <SheetHeader className="sr-only">
              <SheetTitle>Sidebar</SheetTitle>
              <SheetDescription>Displays the mobile sidebar.</SheetDescription>
            </SheetHeader>
            <div className="flex h-full w-full flex-col">{children}</div>
          </SheetContent>
        </Sheet>
      )
    }

    return (
      <div
        ref={ref}
        className="group peer hidden text-sidebar-foreground md:block"
        data-state={state}
        data-collapsible={state === "collapsed" ? collapsible : ""}
        data-variant={variant}
        data-side={side}
      >
        {/* This is what handles the sidebar gap on desktop */}
        <div
          className={cn(
            "relative w-[--sidebar-width] bg-transparent transition-[width] duration-200 ease-linear",
            "group-data-[collapsible=offcanvas]:w-0",
            "group-data-[side=right]:rotate-180",
            variant === "floating" || variant === "inset"
              ? "group-data-[collapsible=icon]:w-[calc(var(--sidebar-width-icon)_+_theme(spacing.4))]"
              : "group-data-[collapsible=icon]:w-[--sidebar-width-icon]"
          )}
        />
        <div
          className={cn(
            "fixed inset-y-0 z-10 hidden h-svh w-[--sidebar-width] transition-[left,right,width] duration-200 ease-linear md:flex",
            side === "left"
              ? "left-0 group-data-[collapsible=offcanvas]:left-[calc(var(--sidebar-width)*-1)]"
              : "right-0 group-data-[collapsible=offcanvas]:right-[calc(var(--sidebar-width)*-1)]",
            // Adjust the padding for floating and inset variants.
            variant === "floating" || variant === "inset"
