                      )}
                    </div>
                    <p className="text-sm text-gray-600 mb-2">{requirement.description}</p>
                    <div className="flex items-center space-x-4 text-xs text-gray-500">
                      <span className="flex items-center">
                        <BookOpen className="w-3 h-3 mr-1" />
                        {requirement.subject}
                      </span>
                      <span className="flex items-center">
                        <Award className="w-3 h-3 mr-1" />
                        {requirement.creditsRequired} credits required
                      </span>
                      {requirement.gradeLevel && (
                        <span className="flex items-center">
                          <GraduationCap className="w-3 h-3 mr-1" />
                          Grades {requirement.gradeLevel}
                        </span>
                      )}
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="text-sm font-medium text-gray-900">
                      {reqProgress.earned.toFixed(1)} / {reqProgress.required} credits
                    </div>
                    <div className="text-xs text-gray-500">
                      {reqProgress.percentage.toFixed(0)}% complete
                    </div>
                  </div>
                </div>

                <Progress value={reqProgress.percentage} className="h-2 mb-3" />

                {reqProgress.courses.length > 0 && (
                  <div className="space-y-2">
                    <h4 className="text-sm font-medium text-gray-700">Completed Courses:</h4>
                    <div className="space-y-1">
                      {reqProgress.courses.map((course) => (
                        <div key={course.id} className="flex items-center justify-between text-sm bg-white p-2 rounded border">
                          <div className="flex items-center space-x-2">
                            <span className="font-medium">{course.courseName}</span>
                            {course.grade && (
                              <Badge variant="outline" className="text-xs">
                                Grade: {course.grade}
                              </Badge>
                            )}
                            {course.semester && (
                              <span className="text-gray-500">({course.semester})</span>
                            )}
                          </div>
                          <span className="text-gray-700">{course.creditsEarned} credits</span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {requirement.alternatives && requirement.alternatives.length > 0 && (
                  <div className="mt-3 p-2 bg-blue-50 rounded text-xs">
                    <span className="font-medium text-blue-700">Alternative courses:</span>
                    <span className="text-blue-600 ml-1">{requirement.alternatives.join(', ')}</span>
                  </div>
                )}
              </div>
            );
          })}

          {requirements.length === 0 && (
            <div className="text-center py-8 text-gray-500">
              <GraduationCap className="h-12 w-12 mx-auto mb-4 text-gray-300" />
              <p className="font-medium">No graduation requirements found</p>
              <p className="text-sm mt-1">Requirements will be based on your state and district.</p>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/AIChatWidget.tsx.content:
import { useState } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Send } from "lucide-react";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import AIMentorIcon from "./AIMentorIcon";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export default function AIChatWidget() {
  const [message, setMessage] = useState("");
  const { toast } = useToast();

  const { data: chatMessages, refetch } = useQuery({
    queryKey: ["/api/student/chat"],
    refetchInterval: 30000, // Refetch every 30 seconds
  });

  const sendMessageMutation = useMutation({
    mutationFn: async (message: string) => {
      const response = await apiRequest("POST", "/api/student/chat", { message });
      return response.json();
    },
    onSuccess: () => {
      setMessage("");
      refetch(); // Immediately refresh chat messages
      toast({
        title: "Message sent!",
        description: "Your AI mentor is thinking...",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    if (message.trim()) {
      sendMessageMutation.mutate(message);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Get the latest AI message for display
  const latestAiMessage = Array.isArray(chatMessages) ? chatMessages.find((msg: any) => msg.sender === "ai") : null;

  return (
    <Card className="shadow-sm border border-gray-200 w-full">
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="text-base sm:text-lg font-semibold text-gray-900 flex items-center">
            <AIMentorIcon className="mr-2 h-4 w-4 sm:h-5 sm:w-5" />
            AI Mentor
          </CardTitle>
          <div className="w-3 h-3 bg-secondary rounded-full animate-pulse"></div>
        </div>
      </CardHeader>
      <CardContent className="pt-0">
        <div className="space-y-3 mb-4 max-h-48 sm:max-h-56 overflow-y-auto overscroll-contain chat-scrollbar">
          <div className="flex items-start space-x-2">
            <div className="w-8 h-8 bg-gradient-to-br from-primary to-secondary rounded-full flex items-center justify-center flex-shrink-0 animate-pulse-soft">
              <AIMentorIcon className="text-white h-5 w-5" />
            </div>
            <div className="bg-gray-50 rounded-lg p-4 text-sm rounded-bl-sm flex-1 border border-gray-200">
              <div className="prose prose-sm max-w-none">
                <ReactMarkdown 
                  remarkPlugins={[remarkGfm]}
                  components={{
                    // Custom components for better formatting
                    h1: ({children}) => <h1 className="text-lg font-bold text-gray-900 mb-2">{children}</h1>,
                    h2: ({children}) => <h2 className="text-base font-semibold text-gray-800 mb-2">{children}</h2>,
                    h3: ({children}) => <h3 className="text-sm font-semibold text-gray-700 mb-1">{children}</h3>,
                    p: ({children}) => <p className="text-gray-700 mb-2 leading-relaxed">{children}</p>,
                    ul: ({children}) => <ul className="list-disc list-inside space-y-1 mb-2 text-gray-700">{children}</ul>,
                    ol: ({children}) => <ol className="list-decimal list-inside space-y-1 mb-2 text-gray-700">{children}</ol>,
                    li: ({children}) => <li className="ml-2">{children}</li>,
                    strong: ({children}) => <strong className="font-semibold text-gray-900">{children}</strong>,
                    em: ({children}) => <em className="italic text-gray-700">{children}</em>,
                    code: ({children}) => <code className="bg-gray-100 px-1 py-0.5 rounded text-xs font-mono text-gray-800">{children}</code>,
                    blockquote: ({children}) => <blockquote className="border-l-2 border-primary pl-3 italic text-gray-600 my-2">{children}</blockquote>
                  }}
                >
                  {latestAiMessage ? latestAiMessage.message : (
                    `**Welcome to AIMS!** 🎓

I'm your **AI Academic Mentor**, here to help guide your journey to your dream college.

**I can help you with:**
• Academic planning and course selection
• College preparation strategies  
• Finding relevant competitions and opportunities
• Extracurricular activity recommendations
• Test preparation guidance
• Personal development planning

**What would you like to discuss today?** Feel free to ask me anything about your academic path!`
                  )}
                </ReactMarkdown>
              </div>
            </div>
          </div>
        </div>
        <div className="flex items-center space-x-2">
          <Input
            type="text"
            placeholder="Ask me anything..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            className="flex-1 text-sm focus:ring-2 focus:ring-primary focus:border-transparent h-9 sm:h-10 chat-input"
            disabled={sendMessageMutation.isPending}
          />
          <Button
            onClick={handleSendMessage}
            disabled={sendMessageMutation.isPending || !message.trim()}
            className="bg-primary hover:bg-primary/90 h-9 w-9 sm:h-10 sm:w-10 p-0 flex-shrink-0 mobile-touch-target"
          >
            {sendMessageMutation.isPending ? (
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
            ) : (
              <Send className="h-4 w-4" />
            )}
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/Achievements.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Achievement, insertAchievementSchema } from "@shared/schema";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogDescription } from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { 
  Trophy, 
  Award, 
  BookOpen, 
  FileText, 
  Plus, 
  Edit2, 
  Trash2, 
  ExternalLink,
  Calendar as CalendarIcon,
  Tag,
  MapPin,
  Building,
  Star
} from "lucide-react";
import { cn } from "@/lib/utils";
import { format } from "date-fns";

const achievementFormSchema = insertAchievementSchema.extend({
  skills: z.array(z.string()).optional(),
}).omit({ studentId: true, createdAt: true, updatedAt: true });

type AchievementFormData = z.infer<typeof achievementFormSchema>;

export default function Achievements() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [editingAchievement, setEditingAchievement] = useState<Achievement | null>(null);
  const [skillInput, setSkillInput] = useState("");

  const { data: achievements = [], isLoading } = useQuery<Achievement[]>({
    queryKey: ["/api/student/achievements"],
  });

  const form = useForm<AchievementFormData>({
    resolver: zodResolver(achievementFormSchema),
    defaultValues: {
      title: "",
      description: "",
      type: "competition",
      category: "academic",
      organization: "",
      location: "",
      ranking: "",
      certificateUrl: "",
      publicationUrl: "",
      skills: [],
      isVerified: false,
      verificationNotes: "",
      dateAchieved: undefined,
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: AchievementFormData) => {
      const response = await apiRequest("POST", "/api/student/achievements", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/student/achievements"] });
      setIsCreateDialogOpen(false);
      form.reset();
      toast({
        title: "Achievement Added!",
        description: "Your achievement has been successfully recorded.",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to add achievement. Please try again.",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: Partial<AchievementFormData> }) => {
      const response = await apiRequest("PATCH", `/api/student/achievements/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/student/achievements"] });
      setEditingAchievement(null);
      form.reset();
      toast({
        title: "Achievement Updated!",
        description: "Your achievement has been successfully updated.",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to update achievement. Please try again.",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await apiRequest("DELETE", `/api/student/achievements/${id}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/student/achievements"] });
      toast({
        title: "Achievement Deleted",
        description: "The achievement has been removed from your profile.",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to delete achievement. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: AchievementFormData) => {
    console.log("Form submitted with data:", data);
    console.log("Form errors:", form.formState.errors);
    console.log("Form is valid:", form.formState.isValid);

    if (editingAchievement) {
      updateMutation.mutate({ id: editingAchievement.id, data });
    } else {
      createMutation.mutate(data);
    }
  };

  const handleEdit = (achievement: Achievement) => {
    setEditingAchievement(achievement);
    form.reset({
      ...achievement,
      dateAchieved: achievement.dateAchieved ? new Date(achievement.dateAchieved) : undefined,
      skills: achievement.skills || [],
    });
    setIsCreateDialogOpen(true);
  };

  const handleDelete = (id: number) => {
    if (confirm("Are you sure you want to delete this achievement?")) {
      deleteMutation.mutate(id);
    }
  };

  const addSkill = () => {
    if (skillInput.trim()) {
      const currentSkills = form.getValues("skills") || [];
      if (!currentSkills.includes(skillInput.trim())) {
        form.setValue("skills", [...currentSkills, skillInput.trim()]);
        setSkillInput("");
      }
    }
  };

  const removeSkill = (skillToRemove: string) => {
    const currentSkills = form.getValues("skills") || [];
    form.setValue("skills", currentSkills.filter(skill => skill !== skillToRemove));
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case "competition": return <Trophy className="w-5 h-5" />;
      case "research": return <BookOpen className="w-5 h-5" />;
      case "certification": return <FileText className="w-5 h-5" />;
      case "award": return <Award className="w-5 h-5" />;
      default: return <Star className="w-5 h-5" />;
    }
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case "competition": return "bg-blue-100 text-blue-800";
      case "research": return "bg-green-100 text-green-800";
      case "certification": return "bg-purple-100 text-purple-800";
      case "award": return "bg-yellow-100 text-yellow-800";
      default: return "bg-gray-100 text-gray-800";
    }
  };

  const getCategoryColor = (category: string) => {
    switch (category) {
      case "academic": return "bg-indigo-100 text-indigo-800";
      case "extracurricular": return "bg-orange-100 text-orange-800";
      case "leadership": return "bg-red-100 text-red-800";
      case "technical": return "bg-teal-100 text-teal-800";
      default: return "bg-gray-100 text-gray-800";
    }
  };

  const groupedAchievements = achievements.reduce((acc, achievement) => {
    const type = achievement.type || "other";
    if (!acc[type]) acc[type] = [];
    acc[type].push(achievement);
    return acc;
  }, {} as Record<string, Achievement[]>);

  if (isLoading) {
    return (
      <Card className="shadow-sm border border-gray-200">
        <CardHeader>
          <CardTitle className="text-xl font-semibold text-gray-900">Achievements</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-20 bg-gray-200 rounded animate-pulse" />
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="shadow-sm border border-gray-200">
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="text-xl font-semibold text-gray-900">Achievements</CardTitle>
          <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
            <DialogTrigger asChild>
              <Button 
                size="sm" 
                onClick={() => {
                  setEditingAchievement(null);
                  form.reset({
                    title: "",
                    description: "",
                    type: "competition",
                    category: "academic",
                    organization: "",
                    location: "",
                    ranking: "",
                    certificateUrl: "",
                    publicationUrl: "",
                    skills: [],
                    isVerified: false,
                    verificationNotes: "",
                    dateAchieved: undefined,
                  });
                }}
              >
                <Plus className="w-4 h-4 mr-2" />
                Add Achievement
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>
                  {editingAchievement ? "Edit Achievement" : "Add New Achievement"}
                </DialogTitle>
                <DialogDescription>
                  {editingAchievement 
                    ? "Update your achievement details below." 
                    : "Add a new achievement to your profile. Include competitions, research papers, certifications, or awards."
                  }
                </DialogDescription>
              </DialogHeader>

              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="title"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Achievement Title *</FormLabel>
                          <FormControl>
                            <Input placeholder="e.g., First Place in Science Fair" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="type"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Type *</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select type" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="competition">Competition</SelectItem>
                              <SelectItem value="research">Research</SelectItem>
                              <SelectItem value="certification">Certification</SelectItem>
                              <SelectItem value="award">Award</SelectItem>
                              <SelectItem value="other">Other</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Description</FormLabel>
                        <FormControl>
                          <Textarea 
                            placeholder="Describe your achievement, what you accomplished, and its significance..."
                            {...field} 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="category"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Category</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select category" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="academic">Academic</SelectItem>
                              <SelectItem value="extracurricular">Extracurricular</SelectItem>
                              <SelectItem value="leadership">Leadership</SelectItem>
                              <SelectItem value="technical">Technical</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="dateAchieved"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Date Achieved</FormLabel>
                          <Popover>
                            <PopoverTrigger asChild>
                              <FormControl>
                                <Button
                                  variant="outline"
                                  className={cn(
                                    "w-full pl-3 text-left font-normal",
                                    !field.value && "text-muted-foreground"
                                  )}
                                >
                                  {field.value ? (
                                    format(field.value, "PPP")
                                  ) : (
                                    <span>Pick a date</span>
                                  )}
                                  <CalendarIcon className="ml-auto h-4 w-4 opacity-50" />
                                </Button>
                              </FormControl>
                            </PopoverTrigger>
                            <PopoverContent className="w-auto p-0" align="start">
                              <Calendar
                                mode="single"
                                selected={field.value}
                                onSelect={field.onChange}
                                disabled={(date) =>
                                  date > new Date() || date < new Date("1900-01-01")
                                }
                                initialFocus
                              />
                            </PopoverContent>
                          </Popover>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="organization"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Organization</FormLabel>
                          <FormControl>
                            <Input placeholder="e.g., Google, MIT, Science Fair" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="location"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Location</FormLabel>
                          <FormControl>
                            <Input placeholder="e.g., San Francisco, CA" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="ranking"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Ranking/Result</FormLabel>
                        <FormControl>
                          <Input placeholder="e.g., 1st Place, Finalist, Honorable Mention" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="certificateUrl"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Certificate/Proof URL</FormLabel>
                          <FormControl>
                            <Input placeholder="https://..." {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="publicationUrl"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Publication URL</FormLabel>
                          <FormControl>
                            <Input placeholder="https://..." {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="skills"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Related Skills</FormLabel>
                        <div className="space-y-2">
                          <div className="flex space-x-2">
                            <Input
                              placeholder="Add a skill..."
                              value={skillInput}
                              onChange={(e) => setSkillInput(e.target.value)}
                              onKeyDown={(e) => {
                                if (e.key === 'Enter') {
                                  e.preventDefault();
                                  addSkill();
                                }
                              }}
                            />
                            <Button type="button" variant="outline" onClick={addSkill}>
                              <Plus className="w-4 h-4" />
                            </Button>
                          </div>
                          {field.value && field.value.length > 0 && (
                            <div className="flex flex-wrap gap-2">
                              {field.value.map((skill, index) => (
                                <Badge key={index} variant="secondary" className="pr-1">
                                  {skill}
                                  <Button
                                    type="button"
                                    variant="ghost"
                                    size="sm"
                                    className="ml-1 h-auto p-0 text-muted-foreground hover:text-foreground"
                                    onClick={() => removeSkill(skill)}
                                  >
                                    ×
                                  </Button>
                                </Badge>
                              ))}
                            </div>
                          )}
                        </div>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="flex justify-end space-x-2 pt-4">
                    <Button 
                      type="button" 
                      variant="outline" 
                      onClick={() => setIsCreateDialogOpen(false)}
                    >
                      Cancel
                    </Button>
                    <Button 
                      type="submit" 
                      disabled={createMutation.isPending || updateMutation.isPending}
                    >
                      {editingAchievement ? "Update" : "Add"} Achievement
                    </Button>
                  </div>
                </form>
              </Form>
            </DialogContent>
          </Dialog>
        </div>
      </CardHeader>
      <CardContent>
        {achievements.length === 0 ? (
          <div className="text-center py-8">
            <Trophy className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No achievements yet</h3>
            <p className="text-gray-600 mb-4">
              Start building your achievement portfolio by adding competitions, research, certifications, and awards.
            </p>
            <Button onClick={() => setIsCreateDialogOpen(true)}>
              <Plus className="w-4 h-4 mr-2" />
              Add Your First Achievement
            </Button>
          </div>
        ) : (
          <Tabs defaultValue="all" className="w-full">
            <TabsList className="grid w-full grid-cols-2 sm:grid-cols-3 lg:grid-cols-5 gap-1 sm:gap-0">
              <TabsTrigger value="all">All</TabsTrigger>
              <TabsTrigger value="competition">Competitions</TabsTrigger>
              <TabsTrigger value="research">Research</TabsTrigger>
              <TabsTrigger value="certification">Certifications</TabsTrigger>
              <TabsTrigger value="award">Awards</TabsTrigger>
            </TabsList>

            <TabsContent value="all" className="space-y-4 mt-4">
              {Object.entries(groupedAchievements).map(([type, typeAchievements]) => (
                <div key={type}>
                  <h3 className="text-lg font-semibold text-gray-900 mb-3 capitalize flex items-center">
                    {getTypeIcon(type)}
                    <span className="ml-2">{type}s ({typeAchievements.length})</span>
                  </h3>
                  <div className="grid gap-4">
                    {typeAchievements.map((achievement) => (
                      <AchievementCard 
                        key={achievement.id} 
                        achievement={achievement}
                        onEdit={handleEdit}
                        onDelete={handleDelete}
                        getTypeColor={getTypeColor}
                        getCategoryColor={getCategoryColor}
                      />
                    ))}
                  </div>
                </div>
              ))}
            </TabsContent>

            {Object.entries(groupedAchievements).map(([type, typeAchievements]) => (
              <TabsContent key={type} value={type} className="space-y-4 mt-4">
                <div className="grid gap-4">
                  {typeAchievements.map((achievement) => (
                    <AchievementCard 
                      key={achievement.id} 
                      achievement={achievement}
                      onEdit={handleEdit}
                      onDelete={handleDelete}
                      getTypeColor={getTypeColor}
                      getCategoryColor={getCategoryColor}
                    />
                  ))}
                </div>
              </TabsContent>
            ))}
          </Tabs>
        )}
      </CardContent>
    </Card>
  );
}

interface AchievementCardProps {
  achievement: Achievement;
  onEdit: (achievement: Achievement) => void;
  onDelete: (id: number) => void;
  getTypeColor: (type: string) => string;
  getCategoryColor: (category: string) => string;
}

function AchievementCard({ achievement, onEdit, onDelete, getTypeColor, getCategoryColor }: AchievementCardProps) {
  return (
    <div className="border border-gray-200 rounded-lg p-4 hover:border-primary transition-colors">
      <div className="flex items-start justify-between">
        <div className="flex-1">
          <div className="flex items-start justify-between mb-2">
            <h4 className="font-semibold text-gray-900">{achievement.title}</h4>
            <div className="flex items-center space-x-2">
              <Badge className={getTypeColor(achievement.type || "other")}>
                {achievement.type}
              </Badge>
              {achievement.category && (
                <Badge className={getCategoryColor(achievement.category)}>
                  {achievement.category}
                </Badge>
              )}
            </div>
          </div>

          {achievement.description && (
            <p className="text-sm text-gray-600 mb-3">{achievement.description}</p>
          )}

          <div className="flex flex-wrap items-center gap-4 text-xs text-gray-500 mb-3">
            {achievement.organization && (
              <div className="flex items-center">
                <Building className="w-3 h-3 mr-1" />
                {achievement.organization}
              </div>
            )}
            {achievement.location && (
              <div className="flex items-center">
                <MapPin className="w-3 h-3 mr-1" />
                {achievement.location}
              </div>
            )}
            {achievement.dateAchieved && (
              <div className="flex items-center">
                <CalendarIcon className="w-3 h-3 mr-1" />
                {format(new Date(achievement.dateAchieved), "MMM yyyy")}
              </div>
            )}
            {achievement.ranking && (
              <div className="flex items-center">
                <Trophy className="w-3 h-3 mr-1" />
                {achievement.ranking}
              </div>
            )}
          </div>

          {achievement.skills && achievement.skills.length > 0 && (
            <div className="flex flex-wrap gap-1 mb-3">
              {achievement.skills.map((skill, index) => (
                <Badge key={index} variant="outline" className="text-xs">
                  <Tag className="w-3 h-3 mr-1" />
                  {skill}
                </Badge>
              ))}
            </div>
          )}

          <div className="flex items-center space-x-3">
            {achievement.certificateUrl && (
              <a
                href={achievement.certificateUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="text-xs text-blue-600 hover:text-blue-800 flex items-center"
              >
                <FileText className="w-3 h-3 mr-1" />
                Certificate
                <ExternalLink className="w-3 h-3 ml-1" />
              </a>
            )}
            {achievement.publicationUrl && (
              <a
                href={achievement.publicationUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="text-xs text-blue-600 hover:text-blue-800 flex items-center"
              >
                <BookOpen className="w-3 h-3 mr-1" />
                Publication
                <ExternalLink className="w-3 h-3 ml-1" />
              </a>
            )}
          </div>
        </div>

        <div className="flex items-center space-x-2 ml-4">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => onEdit(achievement)}
          >
            <Edit2 className="w-4 h-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => onDelete(achievement.id)}
            className="text-red-600 hover:text-red-800"
          >
            <Trash2 className="w-4 h-4" />
          </Button>
        </div>
      </div>
    </div>
  );
}

